                                        const button = e.currentTarget;
                                        const moveButton = () => {
                                            const randomX = (Math.random() - 0.5) * 200;
                                            const randomY = (Math.random() - 0.5) * 100;
                                            button.style.transform = `translate(${randomX}px, ${randomY}px) scale(0.5)`;
                                        };
                                        moveButton();
                                        button.style.transition = 'transform 0.15s ease-out';
                                    }
                                }}
                                onMouseMove={(e) => {
                                    if (!formData.email || !formData.password) {
                                        const button = e.currentTarget;
                                        const randomX = (Math.random() - 0.5) * 200;
                                        const randomY = (Math.random() - 0.5) * 100;
                                        button.style.transform = `translate(${randomX}px, ${randomY}px) scale(0.5)`;
                                    }
                                }}
                                onMouseLeave={(e) => {
                                    if (!formData.email || !formData.password) {
                                        e.currentTarget.style.transform = 'translate(0, 0) scale(1)';
                                        e.currentTarget.style.transition = 'transform 0.3s ease-out';
                                    }
                                }}
                                style={{
                                    position: (!formData.email || !formData.password) ? 'relative' : 'static',
                                    cursor: (!formData.email || !formData.password) ? 'pointer' : 'pointer'
                                }}
                            >
                                {isLoading ? (
                                    <span>Please wait...</span>
                                ) : (
                                    <>
                                        <span>{mode === 'signin' ? 'Sign In' : 'Create Account'}</span>
                                        <ArrowRight size={18} />
                                    </>
                                )}
                            </button>
                        </form>

                        <div className="signin-mode-toggle">
                            <span>
                                {mode === 'signin' 
                                    ? "Don't have an account?" 
                                    : 'Already have an account?'
                                }
                            </span>
                            <button
                                onClick={() => {
                                    setMode(mode === 'signin' ? 'signup' : 'signin');
                                    setError('');
                                }}
                                className="signin-link"
                            >
                                {mode === 'signin' ? 'Sign up' : 'Sign in'}
                            </button>
                        </div>

                        <p className="signin-footer-text">
                            By continuing, you agree to our{' '}
                            <a href="#" className="signin-link">Terms of Service</a>
                            {' '}and{' '}
                            <a href="#" className="signin-link">Privacy Policy</a>
                        </p>
                    </div>

                    {/* Stats */}
                    <div className="signin-stats" style={{ animation: 'fadeInUp 0.8s ease-out 0.6s backwards' }}>
                        <div className="signin-stat" style={{ animation: 'scaleIn 0.5s ease-out 0.8s backwards' }}>
                            <div className="signin-stat-value">Fast</div>
                            <div className="signin-stat-label">Setup Time</div>
                        </div>
                        <div className="signin-stat-divider"></div>
                        <div className="signin-stat" style={{ animation: 'scaleIn 0.5s ease-out 1s backwards' }}>
                            <div className="signin-stat-value">Free</div>
                            <div className="signin-stat-label">To Use</div>
                        </div>
                        <div className="signin-stat-divider"></div>
                        <div className="signin-stat" style={{ animation: 'scaleIn 0.5s ease-out 1.2s backwards' }}>
                            <div className="signin-stat-value">Open</div>
                            <div className="signin-stat-label">Source</div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { useSession, signOut } from 'next-auth/react';
import { MockApi, Resource, Project } from '@/types';
import ApiForm from '@/components/ApiForm';
import ApiList from '@/components/ApiList';
import ResourceForm from '@/components/ResourceForm';
import ResourceList from '@/components/ResourceList';
import ProjectForm from '@/components/ProjectForm';
import ProjectList from '@/components/ProjectList';
import ProfileForm from '@/components/ProfileForm';
import AdminUserManagement from '@/components/AdminUserManagement';
import { Plus, ArrowLeft, LogOut, User, Shield } from 'lucide-react';

type Tab = 'apis' | 'resources';

export default function Home() {
  const { data: session } = useSession();
  const [projects, setProjects] = useState<Project[]>([]);
  const [selectedProject, setSelectedProject] = useState<Project | null>(null);
  const [showProjectForm, setShowProjectForm] = useState(false);
  const [editingProject, setEditingProject] = useState<Project | undefined>(undefined);
  const [showProfile, setShowProfile] = useState(false);
  const [showAdmin, setShowAdmin] = useState(false);
  const [userProfile, setUserProfile] = useState<{firstName?: string; lastName?: string; role?: 'user' | 'admin'} | null>(null);

  const [activeTab, setActiveTab] = useState<Tab>('apis');
  const [apis, setApis] = useState<MockApi[]>([]);
  const [resources, setResources] = useState<Resource[]>([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [editingApi, setEditingApi] = useState<MockApi | undefined>(undefined);
  const [editingResource, setEditingResource] = useState<Resource | undefined>(undefined);

  const fetchProjects = async () => {
    try {
      const res = await fetch('/api/projects');
      const data = await res.json();
      setProjects(data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const fetchApis = async () => {
    if (!selectedProject) return;
    try {
      const res = await fetch('/api/apis');
      const data = await res.json();
      // Filter by project
      setApis(data.filter((api: MockApi) => api.projectId === selectedProject.id));
    } catch (err) {
      console.error(err);
    }
  };

  const fetchResources = async () => {
    if (!selectedProject) return;
    try {
      const res = await fetch('/api/resources');
      const data = await res.json();
      // Filter by project
      setResources(data.filter((r: Resource) => r.projectId === selectedProject.id));
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProjects();
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const res = await fetch('/api/profile');
      if (res.ok) {
        const data = await res.json();
        setUserProfile({ firstName: data.firstName, lastName: data.lastName, role: data.role });
      }
    } catch (err) {
      console.error('Failed to fetch user profile:', err);
    }
  };

  useEffect(() => {
    if (selectedProject) {
      fetchApis();
      fetchResources();
    }
  }, [selectedProject]);

  const handleDeleteProject = async (id: string) => {
    await fetch(`/api/projects/${id}`, { method: 'DELETE' });
    if (selectedProject?.id === id) {
      setSelectedProject(null);
    }
    fetchProjects();
  };

  const handleDeleteApi = async (id: string) => {
    if (!confirm('Are you sure you want to delete this API?')) return;
    await fetch(`/api/apis/${id}`, { method: 'DELETE' });
    fetchApis();
  };

  const handleDeleteResource = async (id: string) => {
    if (!confirm('Are you sure you want to delete this resource?')) return;
    await fetch(`/api/resources/${id}`, { method: 'DELETE' });
    fetchResources();
  };

  const handleEditApi = (api: MockApi) => {
    setEditingApi(api);
    setEditingResource(undefined);
    setShowForm(true);
  };

  const handleEditResource = (resource: Resource) => {
    setEditingResource(resource);
    setEditingApi(undefined);
    setShowForm(true);
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingApi(undefined);
    setEditingResource(undefined);
  };

  const handleSuccess = () => {
    setShowForm(false);
    setEditingApi(undefined);
    setEditingResource(undefined);
    if (activeTab === 'apis') {
      fetchApis();
    } else {
      fetchResources();
    }
  };

  const handleCreateNew = () => {
    setEditingApi(undefined);
    setEditingResource(undefined);
    setShowForm(true);
  };

  const handleProjectCreated = (project: Project) => {
    setShowProjectForm(false);
    setEditingProject(undefined);
    setSelectedProject(project);
    fetchProjects();
  };

  const handleEditProject = (project: Project) => {
    setEditingProject(project);
    setShowProjectForm(true);
  };

  // Project selection view
  if (!selectedProject) {
    return (
      <>
        <header className="app-header">
          <div className="container">
            <div className="flex justify-between items-center">
              <div className="flex items-center gap-6">
                <div className="logo">FakeAPI</div>
                <nav className="flex gap-1">
                  <button className="tab active">Projects</button>
                </nav>
              </div>
              <div className="flex items-center gap-3">
                {session?.user && (
                  <>
                    {userProfile?.firstName && (
                      <span className="user-greeting">
                        Hi, <strong>{userProfile.firstName} {userProfile.lastName}</strong>!
                      </span>
                    )}
                    {userProfile?.role === 'admin' && (
                      <button
                        onClick={() => setShowAdmin(true)}
                        className="icon-btn"
                        title="Admin Panel"
                      >
                        <Shield size={18} />
                      </button>
                    )}
                    <button
                      onClick={() => setShowProfile(true)}
                      className="icon-btn"
                      title="Profile"
                    >
                      {session.user.image ? (
                        <img
                          src={session.user.image}
                          alt={`${userProfile?.firstName || session.user.name || 'User'}`}
                          className="w-8 h-8 rounded-full"
                        />
                      ) : (
                        <User size={18} />
                      )}
                    </button>
                  </>
                )}
                <button
                  onClick={() => signOut({ callbackUrl: '/auth/signin' })}
                  className="icon-btn"
                  title="Sign out"
                >
                  <LogOut size={18} />
                </button>
              </div>
            </div>
          </div>
        </header>

        <main className="py-12">
          <div className="container">
            <div className="flex justify-between items-center mb-8">
              <div>
                <h1 className="text-2xl font-bold mb-2">Your Projects</h1>
                <p className="text-muted">Create and manage your mock API projects</p>
              </div>
              <button
                onClick={() => {
                  setEditingProject(undefined);
                  setShowProjectForm(true);
                }}
                className="btn btn-primary"
                disabled={showProjectForm}
              >
                <Plus size={18} className="mr-2" />
                New Project
              </button>
            </div>

            {showProfile && (
              <div className="mb-8">
                <ProfileForm
                  onSuccess={() => {
                    setShowProfile(false);
                    fetchUserProfile();
                  }}
                  onCancel={() => setShowProfile(false)}
                />
              </div>
            )}

            {showAdmin && (
              <div className="mb-8">
                <div className="card">
                  <div className="flex justify-between items-center mb-4">
                    <h3 className="text-xl font-semibold">Admin Panel</h3>
                    <button onClick={() => setShowAdmin(false)} className="btn-secondary">Close</button>
                  </div>
                  <AdminUserManagement />
                </div>
              </div>
            )}

            {showProjectForm && (
              <div className="mb-8">
                <ProjectForm
                  initialData={editingProject}
                  onSuccess={handleProjectCreated}
                  onCancel={() => {
                    setShowProjectForm(false);
                    setEditingProject(undefined);
                  }}
                />
              </div>
            )}

            {loading ? (
              <div className="empty-state">
                <div className="text-lg">Loading...</div>
              </div>
            ) : (
              <ProjectList
                projects={projects}
                onSelect={setSelectedProject}
                onDelete={handleDeleteProject}
                onEdit={handleEditProject}
                onUpdate={fetchProjects}
              />
            )}
          </div>
        </main>
      </>
    );
  }

  // Project workspace view
  return (
    <>
      <header className="app-header">
        <div className="container">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-6">
              <div className="logo">FakeAPI</div>
              <nav className="flex items-center gap-4">
                <button
                  onClick={() => {
                    setSelectedProject(null);
                    setShowForm(false);
                  }}
                  className="btn-ghost flex items-center gap-2"
                >
                  <ArrowLeft size={16} />
                  Projects
                </button>
                <span className="text-muted">/</span>
                <div className="flex items-center gap-2">
                  <div className="w-2 h-2 rounded-full bg-primary"></div>
                  <span className="font-medium">{selectedProject.name}</span>
                </div>
              </nav>
            </div>
            <div className="flex items-center gap-3">
              {session?.user && (
                <>
                  {userProfile?.firstName && (
                    <span className="user-greeting">
                      Hi, <strong>{userProfile.firstName} {userProfile.lastName}</strong>!
                    </span>
                  )}
                  <button
                    onClick={() => setShowProfile(true)}
                    className="icon-btn"
                    title="Profile"
                  >
                    {session.user.image ? (
                      <img
                        src={session.user.image}
                        alt={`${userProfile?.firstName || session.user.name || 'User'}`}
                        className="w-8 h-8 rounded-full"
                      />
                    ) : (
                      <User size={18} />
                    )}
                  </button>
                </>
              )}
              <button
                onClick={() => signOut({ callbackUrl: '/auth/signin' })}
                className="icon-btn"
                title="Sign out"
              >
                <LogOut size={18} />
              </button>
            </div>
          </div>
        </div>
      </header>

      <main className="py-12">
        <div className="container">
          <div className="flex justify-between items-center mb-6">
            <div>
              <h1 className="text-2xl font-bold mb-1">{selectedProject.name}</h1>
              {selectedProject.description && (
                <p className="text-muted text-sm">{selectedProject.description}</p>
              )}
            </div>
            <button
              onClick={handleCreateNew}
              className="btn btn-primary"
              disabled={showForm}
            >
              <Plus size={18} className="mr-2" />
              {activeTab === 'apis' ? 'New Endpoint' : 'New Resource'}
            </button>
          </div>

          {/* Tabs */}
          <div className="tabs">
            <button
              onClick={() => { setActiveTab('apis'); setShowForm(false); }}
              className={`tab ${activeTab === 'apis' ? 'active' : ''}`}
            >
              Custom APIs ({apis.length})
            </button>
            <button
              onClick={() => { setActiveTab('resources'); setShowForm(false); }}
              className={`tab ${activeTab === 'resources' ? 'active' : ''}`}
            >
              Resources ({resources.length})
            </button>
          </div>

          {showForm && (
            <div className="mb-8">
              {activeTab === 'apis' ? (
                <ApiForm
                  initialData={editingApi}
                  onSuccess={handleSuccess}
                  onCancel={handleCancel}
                  projectId={selectedProject.id}
                />
              ) : (
                <ResourceForm
                  initialData={editingResource}
                  onSuccess={handleSuccess}
                  onCancel={handleCancel}
                  projectId={selectedProject.id}
                />
              )}
            </div>
          )}

          <>
            {activeTab === 'apis' && (
              <ApiList apis={apis} onDelete={handleDeleteApi} onEdit={handleEditApi} />
            )}
            {activeTab === 'resources' && (
              <ResourceList resources={resources} onDelete={handleDeleteResource} onEdit={handleEditResource} />
            )}
          </>
        </div>
      </main>
    </>
  );
}

import NextAuth from "next-auth"
import Google from "next-auth/providers/google"
import Credentials from "next-auth/providers/credentials"
import bcrypt from "bcryptjs"
import connectDB from "@/lib/mongodb"
import { UserProfile as UserProfileModel } from "@/models"

export const { handlers, signIn, signOut, auth } = NextAuth({
    secret: process.env.AUTH_SECRET || process.env.NEXTAUTH_SECRET,
    trustHost: true,
    providers: [
        Google({
            clientId: process.env.GOOGLE_CLIENT_ID,
            clientSecret: process.env.GOOGLE_CLIENT_SECRET,
        }),
        Credentials({
            name: "Credentials",
            credentials: {
                email: { label: "Email", type: "email" },
                password: { label: "Password", type: "password" }
            },
            async authorize(credentials) {
                if (!credentials?.email || !credentials?.password) {
                    throw new Error("Email and password are required");
                }

                await connectDB();

                // Find user by email
                const user = await UserProfileModel.findOne({ 
                    email: (credentials.email as string).toLowerCase() 
                });

                if (!user) {
                    throw new Error("Invalid email or password");
                }

                // Check if user has a password (not OAuth-only)
                if (!user.password) {
                    throw new Error("Please sign in with Google");
                }

                // Verify password
                const isValidPassword = await bcrypt.compare(
                    credentials.password as string,
                    user.password
                );

                if (!isValidPassword) {
                    throw new Error("Invalid email or password");
                }

                // Return user object
                return {
                    id: user._id.toString(),
                    email: user.email,
                    name: user.firstName ? `${user.firstName} ${user.lastName || ''}`.trim() : user.email,
                    image: null,
                };
            }
        })
    ],
    pages: {
        signIn: '/auth/signin',
    },
    callbacks: {
        authorized: async ({ auth }) => {
            // Logged in users are authenticated, otherwise redirect to login page
            return !!auth
        },
        async signIn({ user, account, profile }) {
            // For Google OAuth, save user to MongoDB
            if (account?.provider === "google") {
                await connectDB();

                const existingUser = await UserProfileModel.findOne({ 
                    email: user.email?.toLowerCase() 
                });

                if (!existingUser && user.email) {
                    // Extract first and last name from profile or user.name
                    let firstName = "";
                    let lastName = "";

                    if (profile?.name) {
                        const nameParts = (profile.name as string).split(" ");
                        firstName = nameParts[0] || "";
                        lastName = nameParts.slice(1).join(" ") || "";
                    } else if (user.name) {
                        const nameParts = user.name.split(" ");
                        firstName = nameParts[0] || "";
                        lastName = nameParts.slice(1).join(" ") || "";
                    }

                    await UserProfileModel.create({
                        email: user.email.toLowerCase(),
                        firstName,
                        lastName,
                        role: 'user',
                        isEmailVerified: true, // Google OAuth emails are verified
                    });
                }
            }

            return true;
        },
        async session({ session, token }) {
            // Add user data to session
            if (session.user && token.email) {
                await connectDB();
                const user = await UserProfileModel.findOne({ 
                    email: token.email.toLowerCase() 
                });

                if (user) {
                    (session.user as any).role = user.role || 'user';
                    (session.user as any).firstName = user.firstName;
                    (session.user as any).lastName = user.lastName;
                }
            }
            return session;
        },
    },
    session: {
        strategy: "jwt", // Use JWT for session management
    },
})

'use client';

import { useState, useEffect } from 'react';
import { Trash2, Shield, User } from 'lucide-react';

interface UserWithStats {
  _id: string;
  email: string;
  firstName?: string;
  lastName?: string;
  role?: 'user' | 'admin';
  projectCount: number;
  collaboratingCount: number;
  createdAt: string;
  updatedAt: string;
}

export default function AdminUserManagement() {
  const [users, setUsers] = useState<UserWithStats[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  async function fetchUsers() {
    try {
      setLoading(true);
      const res = await fetch('/api/admin/users');
      if (!res.ok) {
        const data = await res.json();
        throw new Error(data.error || 'Failed to fetch users');
      }
      const data = await res.json();
      setUsers(data);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleDeleteUser(email: string) {
    if (!confirm(`Are you sure you want to delete user ${email} and all their projects? This action cannot be undone.`)) {
      return;
    }

    try {
      const res = await fetch(`/api/admin/users/${encodeURIComponent(email)}`, {
        method: 'DELETE',
      });

      if (!res.ok) {
        const data = await res.json();
        throw new Error(data.error || 'Failed to delete user');
      }

      const result = await res.json();
      alert(`${result.message}\n\nDeleted:\n- ${result.deletedCounts.projects} projects\n- ${result.deletedCounts.resources} resources\n- ${result.deletedCounts.apis} APIs\n- ${result.deletedCounts.databases} database records`);
      fetchUsers();
    } catch (err: any) {
      alert('Error: ' + err.message);
    }
  }

  async function handleToggleRole(email: string, currentRole: 'user' | 'admin') {
    const newRole = currentRole === 'admin' ? 'user' : 'admin';

    if (!confirm(`Change ${email} role from "${currentRole}" to "${newRole}"?`)) {
      return;
    }

    try {
      const res = await fetch(`/api/admin/users/${encodeURIComponent(email)}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ role: newRole }),
      });

      if (!res.ok) {
        const data = await res.json();
        throw new Error(data.error || 'Failed to update user role');
      }

      fetchUsers();
    } catch (err: any) {
      alert('Error: ' + err.message);
    }
  }

  if (loading) {
    return <div className="admin-loading">Loading users...</div>;
  }

  if (error) {
    return <div className="admin-error">Error: {error}</div>;
  }

  return (
    <div className="admin-container">
      <div className="admin-header">
        <h2>User Management</h2>
        <p className="admin-subtitle">Total Users: {users.length}</p>
      </div>

      <div className="admin-table-container">
        <table className="admin-table">
          <thead>
            <tr>
              <th>User</th>
              <th>Email</th>
              <th>Role</th>
              <th>Projects</th>
              <th>Collaborations</th>
              <th>Joined</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {Array.isArray(users) && users.map((user) => (
              <tr key={user._id}>
                <td>
                  <div className="user-info">
                    <strong>{user.firstName || user.lastName ? `${user.firstName || ''} ${user.lastName || ''}`.trim() : 'Unnamed User'}</strong>
                  </div>
                </td>
                <td>{user.email}</td>
                <td>
                  <span className={`role-badge role-${user.role || 'user'}`}>
                    {user.role === 'admin' ? <Shield size={14} /> : <User size={14} />}
                    {user.role || 'user'}
                  </span>
                </td>
                <td>{user.projectCount}</td>
                <td>{user.collaboratingCount}</td>
                <td>{new Date(user.createdAt).toLocaleDateString()}</td>
                <td>
                  <div className="action-buttons">
                    <button
                      onClick={() => handleToggleRole(user.email, user.role || 'user')}
                      className="btn-role"
                      title={`Change to ${user.role === 'admin' ? 'user' : 'admin'}`}
                    >
                      {user.role === 'admin' ? <User size={16} /> : <Shield size={16} />}
                    </button>
                    <button
                      onClick={() => handleDeleteUser(user.email)}
                      className="btn-delete"
                      title="Delete user and all projects"
                    >
                      <Trash2 size={16} />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

