    );
}

'use client';

import { useState, useEffect } from 'react';
import { UserProfile } from '@/types';
import { Save, X, User as UserIcon, Mail, Phone, Sparkles, Trash2 } from 'lucide-react';

interface ProfileFormProps {
  onSuccess: () => void;
  onCancel: () => void;
}

export default function ProfileForm({ onSuccess, onCancel }: ProfileFormProps) {
  const [profile, setProfile] = useState<UserProfile>({
    email: '',
    firstName: '',
    lastName: '',
    mobile: '',
    updatedAt: '',
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [deleting, setDeleting] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const res = await fetch('/api/profile');
      if (!res.ok) throw new Error('Failed to fetch profile');
      const data = await res.json();
      setProfile(data);
    } catch (err) {
      setError('Failed to load profile');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSaving(true);
    setError('');

    try {
      const res = await fetch('/api/profile', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          firstName: profile.firstName,
          lastName: profile.lastName,
          mobile: profile.mobile,
        }),
      });

      if (!res.ok) throw new Error('Failed to update profile');

      onSuccess();
    } catch (err) {
      setError('Failed to update profile');
      console.error(err);
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteAccount = async () => {
    setDeleting(true);
    setError('');

    try {
      const res = await fetch('/api/profile', {
        method: 'DELETE',
      });

      if (!res.ok) throw new Error('Failed to delete account');

      // Redirect to sign out after successful deletion
      window.location.href = '/api/auth/signout';
    } catch (err) {
      setError('Failed to delete account');
      console.error(err);
      setDeleting(false);
      setShowDeleteConfirm(false);
    }
  };

  if (loading) {
    return (
      <div className="profile-card-modern">
        <div className="profile-loading">
          <div className="profile-loading-spinner"></div>
          <p>Loading your profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-card-modern">
      {/* Header Section */}
      <div className="profile-header-gradient">
        <div className="profile-header-content">
          <div className="profile-avatar-large">
            <UserIcon size={40} />
          </div>
          <div className="profile-header-text">
            <h1 className="profile-title">
              <Sparkles size={24} className="profile-sparkle" />
              My Profile
            </h1>
            <p className="profile-subtitle">Manage your personal information</p>
          </div>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="profile-form-modern">
        {/* Personal Information Section */}
        <div className="profile-section">
          <div className="profile-section-header">
            <h3 className="profile-section-title">Personal Information</h3>
            <p className="profile-section-desc">Your name and contact details</p>
          </div>

          <div className="profile-fields-grid">
            <div className="profile-field-wrapper">
              <label className="profile-label">
                <UserIcon size={16} />
                First Name
              </label>
              <input
                type="text"
                className="profile-input"
                placeholder="Enter your first name"
                value={profile.firstName || ''}
                onChange={(e) => setProfile({ ...profile, firstName: e.target.value })}
                maxLength={50}
              />
            </div>

            <div className="profile-field-wrapper">
              <label className="profile-label">
                <UserIcon size={16} />
                Last Name
              </label>
              <input
                type="text"
                className="profile-input"
                placeholder="Enter your last name"
                value={profile.lastName || ''}
                onChange={(e) => setProfile({ ...profile, lastName: e.target.value })}
                maxLength={50}
              />
            </div>
          </div>
        </div>

        {/* Contact Information Section */}
        <div className="profile-section">
          <div className="profile-section-header">
            <h3 className="profile-section-title">Contact Information</h3>
            <p className="profile-section-desc">How we can reach you</p>
          </div>

          <div className="profile-field-wrapper">
            <label className="profile-label">
              <Mail size={16} />
              Email Address
            </label>
            <input
              type="email"
              className="profile-input profile-input-disabled"
              value={profile.email}
              disabled
            />
            <p className="profile-field-hint">
              <span className="profile-lock-icon">ðŸ”’</span>
              Your email is provided by Google and cannot be changed
            </p>
          </div>

          <div className="profile-field-wrapper">
            <label className="profile-label">
              <Phone size={16} />
              Mobile Number
            </label>
            <input
              type="tel"
              className="profile-input"
              placeholder="+1 (555) 123-4567"
              value={profile.mobile || ''}
              onChange={(e) => setProfile({ ...profile, mobile: e.target.value })}
              maxLength={20}
            />
            <p className="profile-field-hint">We'll never share your number with anyone</p>
          </div>
        </div>

        {error && (
          <div className="profile-error">
            <span className="profile-error-icon">âš ï¸</span>
            {error}
          </div>
        )}

        {/* Action Buttons */}
        <div className="profile-actions">
          <button
            type="button"
            onClick={onCancel}
            className="profile-btn-secondary"
            disabled={saving || deleting}
          >
            <X size={18} />
            Cancel
          </button>
          <button
            type="submit"
            className="profile-btn-primary"
            disabled={saving || deleting}
          >
            {saving ? (
              <>
                <div className="profile-btn-spinner"></div>
                Saving Changes...
              </>
            ) : (
              <>
                <Save size={18} />
                Save Changes
              </>
            )}
          </button>
        </div>

        {/* Danger Zone */}
        <div className="profile-danger-zone">
          <div className="profile-section-header">
            <h3 className="profile-section-title" style={{ color: '#ef4444' }}>Danger Zone</h3>
            <p className="profile-section-desc">Permanent actions that cannot be undone</p>
          </div>

          {!showDeleteConfirm ? (
            <button
              type="button"
              onClick={() => setShowDeleteConfirm(true)}
              className="profile-btn-danger"
              disabled={saving || deleting}
            >
              <Trash2 size={18} />
              Delete Account
            </button>
          ) : (
            <div className="profile-delete-confirm">
              <p className="profile-delete-warning">
                âš ï¸ <strong>Warning:</strong> This will permanently delete your account, all projects, resources, APIs, and generated data. This action cannot be undone.
              </p>
              <div className="profile-delete-actions">
                <button
                  type="button"
                  onClick={() => setShowDeleteConfirm(false)}
                  className="profile-btn-secondary"
                  disabled={deleting}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  onClick={handleDeleteAccount}
                  className="profile-btn-danger-confirm"
                  disabled={deleting}
                >
                  {deleting ? (
                    <>
                      <div className="profile-btn-spinner"></div>
                      Deleting...
                    </>
                  ) : (
                    <>
                      <Trash2 size={18} />
                      Yes, Delete Everything
                    </>
                  )}
                </button>
              </div>
            </div>
          )}
        </div>
      </form>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Project } from '@/types';

interface ProjectFormProps {
    onSuccess: (project: Project) => void;
    onCancel: () => void;
    initialData?: Project;
}

export default function ProjectForm({ onSuccess, onCancel, initialData }: ProjectFormProps) {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (initialData) {
            setName(initialData.name);
            setDescription(initialData.description || '');
        }
    }, [initialData]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            if (!name.trim()) {
                throw new Error('Project name is required');
            }

            const url = initialData ? `/api/projects/${initialData.id}` : '/api/projects';
            const method = initialData ? 'PUT' : 'POST';

            const res = await fetch(url, {
                method,
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ name, description }),
            });

            if (!res.ok) {
                const data = await res.json();
                throw new Error(data.error || 'Failed to create project');
            }

            const project = await res.json();
            onSuccess(project);
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="card animate-fade-in">
            <h2 className="text-xl mb-4">{initialData ? 'Edit Project' : 'Create New Project'}</h2>

            {error && (
                <div className="mb-4 p-3 bg-error-light rounded text-sm">
                    {error}
                </div>
            )}

            <form onSubmit={handleSubmit} className="flex flex-col gap-4">
                <div>
                    <label className="label">Project Name</label>
                    <input
                        type="text"
                        className="input"
                        placeholder="e.g. E-commerce API, Blog Platform"
                        value={name}
                        onChange={e => setName(e.target.value)}
                        required
                        autoFocus
                    />
                </div>

                <div>
                    <label className="label">Description (Optional)</label>
                    <textarea
                        className="input"
                        rows={3}
                        placeholder="Brief description of your project..."
                        value={description}
                        onChange={e => setDescription(e.target.value)}
                    />
                </div>

                <div className="flex justify-end gap-2 mt-2">
                    <button type="button" onClick={onCancel} className="btn btn-secondary">
                        Cancel
                    </button>
                    <button type="submit" className="btn btn-primary" disabled={loading}>
                        {loading ? (initialData ? 'Updating...' : 'Creating...') : (initialData ? 'Update Project' : 'Create Project')}
                    </button>
                </div>
            </form>
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { Resource, ResourceField, FieldType } from '@/types';
import { FAKER_METHODS } from '@/lib/dataGenerator';
import { Plus, Trash2 } from 'lucide-react';

interface ResourceFormProps {
    onSuccess: () => void;
    onCancel: () => void;
    initialData?: Resource;
    projectId: string;
}

export default function ResourceForm({ onSuccess, onCancel, initialData, projectId }: ResourceFormProps) {
    const [name, setName] = useState('');
    const [fields, setFields] = useState<ResourceField[]>([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [resources, setResources] = useState<Resource[]>([]);

    useEffect(() => {
        fetchResources();
        if (initialData) {
            setName(initialData.name);
            setFields(initialData.fields);
        }
    }, [initialData]);

    const fetchResources = async () => {
        const res = await fetch('/api/resources');
        const data = await res.json();
        setResources(data);
    };

    const addField = () => {
        setFields([
            ...fields,
            {
                id: crypto.randomUUID(),
                name: '',
                type: 'string',
                required: false,
            },
        ]);
    };

    const updateField = (id: string, updates: Partial<ResourceField>) => {
        setFields(fields.map(f => (f.id === id ? { ...f, ...updates } : f)));
    };

    const removeField = (id: string) => {
        setFields(fields.filter(f => f.id !== id));
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            if (!name) {
                throw new Error('Missing required field: Resource Name');
            }

            if (fields.length === 0) {
                throw new Error('At least one field is required. Click "Add Field" to add fields.');
            }

            // Validate resource name format
            if (name.includes('/') || name.includes('\\') || name.includes(' ')) {
                throw new Error('Resource name cannot contain slashes or spaces. Use simple names like "users", "devices", "products". For complex paths like "/devices/chart", use Custom APIs instead.');
            }

            // Validate that all fields have names
            const emptyFields = fields.filter(f => !f.name.trim());
            if (emptyFields.length > 0) {
                throw new Error(`Missing field name for ${emptyFields.length} field(s). All fields must have a name.`);
            }

            const url = initialData ? `/api/resources/${initialData.id}` : '/api/resources';
            const method = initialData ? 'PUT' : 'POST';

            const res = await fetch(url, {
                method,
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ name, fields, projectId }),
            });

            if (!res.ok) {
                const data = await res.json();
                throw new Error(data.error || 'Failed to save resource');
            }

            onSuccess();
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="card animate-fade-in">
            <h2 className="text-xl mb-4">{initialData ? 'Edit Resource' : 'Create New Resource'}</h2>

            {error && (
                <div className="mb-4 p-3 bg-error-light rounded text-sm">
                    {error}
                </div>
            )}

            <form onSubmit={handleSubmit} className="flex flex-col gap-4">
                <div>
                    <label className="label">Resource Name (plural)</label>
                    <input
                        type="text"
                        className="input"
                        placeholder="e.g. users, products, devices"
                        value={name}
                        onChange={e => setName(e.target.value.toLowerCase())}
                        required
                    />
                    <p className="text-xs text-muted mt-1">
                        Endpoint: /api/v1/{name || 'resource'}<br />
                        <span className="text-yellow-500">âš ï¸ No slashes allowed. For paths like "/devices/chart", use Custom APIs.</span>
                    </p>
                </div>

                <div>
                    <div className="flex justify-between items-center mb-2">
                        <label className="label mb-0">Fields</label>
                        <button type="button" onClick={addField} className="btn btn-secondary text-sm">
                            <Plus size={14} className="mr-1" />
                            Add Field
                        </button>
                    </div>

                    {fields.map((field, _index) => (
                        <div key={field.id} className="flex gap-2 mb-2 p-3 bg-dark-muted rounded">
                            <input
                                type="text"
                                className="input flex-1"
                                placeholder="Field name"
                                value={field.name}
                                onChange={e => updateField(field.id, { name: e.target.value })}
                                required
                            />

                            <select
                                className="input w-32"
                                value={field.type}
                                onChange={e => updateField(field.id, { type: e.target.value as FieldType })}
                            >
                                <option value="string">String</option>
                                <option value="number">Number</option>
                                <option value="boolean">Boolean</option>
                                <option value="date">Date</option>
                                <option value="email">Email</option>
                                <option value="uuid">UUID</option>
                                <option value="image">Image</option>
                                <option value="relation">Relation</option>
                            </select>

                            {field.type === 'string' && (
                                <select
                                    className="input w-40"
                                    value={field.fakerMethod || ''}
                                    onChange={e => updateField(field.id, { fakerMethod: e.target.value })}
                                >
                                    <option value="">Random text</option>
                                    {FAKER_METHODS.map(m => (
                                        <option key={m.value} value={m.value}>{m.label}</option>
                                    ))}
                                </select>
                            )}

                            {field.type === 'relation' && (
                                <select
                                    className="input w-32"
                                    value={field.relationTo || ''}
                                    onChange={e => updateField(field.id, { relationTo: e.target.value })}
                                >
                                    <option value="">Select resource</option>
                                    {resources.map(r => (
                                        <option key={r.id} value={r.name}>{r.name}</option>
                                    ))}
                                </select>
                            )}

                            <label className="flex items-center gap-1 text-sm">
                                <input
                                    type="checkbox"
                                    checked={field.required}
                                    onChange={e => updateField(field.id, { required: e.target.checked })}
                                />
                                Required
                            </label>

                            <button
                                type="button"
                                onClick={() => removeField(field.id)}
                                className="text-red hover:text-red-400 p-2"
                            >
                                <Trash2 size={16} />
                            </button>
                        </div>
                    ))}

                    {fields.length === 0 && (
                        <p className="text-muted text-sm text-center py-4">No fields added yet. Click "Add Field" to start.</p>
                    )}
                </div>

                <div className="flex justify-end gap-2 mt-2">
                    <button type="button" onClick={onCancel} className="btn btn-secondary">
                        Cancel
                    </button>
                    <button type="submit" className="btn btn-primary" disabled={loading}>
                        {loading ? 'Saving...' : (initialData ? 'Update Resource' : 'Create Resource')}
                    </button>
                </div>
            </form>
        </div>
    );
}

'use client';

