'use client';

import { useState } from 'react';
import { useSession } from 'next-auth/react';
import { Project } from '@/types';
import { X, UserPlus, Trash2, Copy, Check, Users, Link as LinkIcon, Globe, Crown } from 'lucide-react';

interface ShareModalProps {
  project: Project;
  onClose: () => void;
  onUpdate: () => void;
}

export default function ShareModal({ project, onClose, onUpdate }: ShareModalProps) {
  const { data: session } = useSession();
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [copied, setCopied] = useState(false);

  const isOwner = session?.user?.email === project.userId;
  const apiUrl = `${typeof window !== 'undefined' ? window.location.origin : ''}/api/v1`;

  const handleAddCollaborator = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const res = await fetch(`/api/projects/${project.id}/collaborators`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: email.trim() }),
      });

      const data = await res.json();

      if (!res.ok) {
        throw new Error(data.error || 'Failed to add collaborator');
      }

      setEmail('');
      onUpdate();
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRemoveCollaborator = async (collaboratorEmail: string) => {
    if (!confirm(`Remove ${collaboratorEmail} from this project?`)) return;

    try {
      const res = await fetch(`/api/projects/${project.id}/collaborators`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: collaboratorEmail }),
      });

      if (!res.ok) {
        throw new Error('Failed to remove collaborator');
      }

      onUpdate();
    } catch (err) {
      alert('Failed to remove collaborator');
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(apiUrl);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="share-modal-overlay" onClick={onClose}>
      <div className="share-modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Header */}
        <div className="share-modal-header">
          <div className="flex items-center gap-3">
            <div className="share-modal-icon">
              <Users size={24} />
            </div>
            <div>
              <h2 className="share-modal-title">Share Project</h2>
              <p className="share-modal-subtitle">{project.name}</p>
            </div>
          </div>
          <button onClick={onClose} className="share-modal-close">
            <X size={20} />
          </button>
        </div>

        {/* Public API Access */}
        <div className="share-section">
          <div className="share-section-header">
            <Globe size={18} />
            <h3>Public API Access</h3>
          </div>
          <p className="share-section-desc">
            All generated APIs are publicly accessible. Anyone with the URL can use them.
          </p>
          <div className="share-url-box">
            <LinkIcon size={16} className="share-url-icon" />
            <code className="share-url-text">{apiUrl}</code>
            <button onClick={copyToClipboard} className="share-copy-btn">
              {copied ? <Check size={16} /> : <Copy size={16} />}
            </button>
          </div>
        </div>

        {/* Collaborators Section */}
        <div className="share-section">
          <div className="share-section-header">
            <UserPlus size={18} />
            <h3>Manage Collaborators</h3>
          </div>
          <p className="share-section-desc">
            {isOwner 
              ? 'Invite team members to view and edit this project.'
              : 'You are a collaborator on this project. Only the owner can add or remove collaborators.'}
          </p>

          {/* Add Collaborator Form - Only show for owner */}
          {isOwner && (
            <form onSubmit={handleAddCollaborator} className="share-add-form">
              <input
                type="email"
                className="share-input"
                placeholder="Enter collaborator's email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                disabled={loading}
                required
              />
              <button type="submit" className="share-add-btn" disabled={loading}>
                {loading ? 'Adding...' : 'Add'}
              </button>
            </form>
          )}

          {error && (
            <div className="share-error">
              {error}
            </div>
          )}

          {/* Collaborators List */}
          <div className="share-collaborators-list">
            {/* Owner */}
            <div className="share-collaborator-item">
              <div className="share-collaborator-avatar" style={{ background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)' }}>
                {project.userId.charAt(0).toUpperCase()}
              </div>
              <div style={{ flex: 1 }}>
                <span className="share-collaborator-email">{project.userId}</span>
                <div style={{ fontSize: '11px', color: '#d97706', fontWeight: 500, display: 'flex', alignItems: 'center', gap: '4px', marginTop: '2px' }}>
                  <Crown size={12} />
                  Owner
                </div>
              </div>
            </div>

            {/* Collaborators */}
            {project.collaborators && Array.isArray(project.collaborators) && project.collaborators.length > 0 ? (
              project.collaborators.map((collaborator) => (
                <div key={collaborator} className="share-collaborator-item">
                  <div className="share-collaborator-avatar">
                    {collaborator.charAt(0).toUpperCase()}
                  </div>
                  <span className="share-collaborator-email">{collaborator}</span>
                  {isOwner && (
                    <button
                      onClick={() => handleRemoveCollaborator(collaborator)}
                      className="share-remove-btn"
                      title="Remove collaborator"
                    >
                      <Trash2 size={16} />
                    </button>
                  )}
                </div>
              ))
            ) : (
              !isOwner && (
                <div className="share-empty-state" style={{ padding: '16px' }}>
                  <p style={{ fontSize: '13px' }}>No other collaborators</p>
                </div>
              )
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

import { IFieldGenerator } from './IFieldGenerator';

/**
 * Interface for the main data generation service
 * Coordinates field generators and produces complete data records
 */
export interface IDataGenerator {
  /**
   * Generate a value for a single field
   */
  generateFieldValue(field: any, allData?: any): any;

  /**
   * Generate multiple records for a resource
   */
  generateRecords(resource: any, count: number): any[];

  /**
   * Register a new field generator
   * Allows dynamic extension without modifying the data generator (Open/Closed Principle)
   */
  registerGenerator(generator: IFieldGenerator): void;

  /**
   * Get all supported field types
   */
  getSupportedTypes(): string[];
}

/**
 * Interface for field value generators
 * Each field type should have its own implementation
 * Following the Strategy Pattern for extensibility (Open/Closed Principle)
 */
export interface IFieldGenerator {
  /**
   * Generates a value for a field based on its configuration
   * @param field - The field configuration
   * @param allData - Optional context data for relation fields
   * @returns The generated value
   */
  generate(field: any, allData?: any): any;

  /**
   * Returns the field type this generator handles
   */
  getType(): string;
}

/**
 * Generic repository interface for data access
 * Abstracts away data layer details (MongoDB, file system, etc.)
 * Following the Dependency Inversion Principle
 */
export interface IRepository<T> {
  /**
   * Find a document by ID
   */
  findById(id: string): Promise<T | null>;

  /**
   * Find all documents matching the filter
   */
  find(filter?: Record<string, any>): Promise<T[]>;

  /**
   * Find one document matching the filter
   */
  findOne(filter: Record<string, any>): Promise<T | null>;

  /**
   * Create a new document
   */
  create(data: Partial<T>): Promise<T>;

  /**
   * Update a document by ID
   */
  update(id: string, data: Partial<T>): Promise<T | null>;

  /**
   * Delete a document by ID
   */
  delete(id: string): Promise<boolean>;

  /**
   * Check if a document exists
   */
  exists(filter: Record<string, any>): Promise<boolean>;

  /**
   * Count documents matching the filter
   */
  count(filter?: Record<string, any>): Promise<number>;
}

/**
 * Base service interface
 * Services encapsulate business logic and coordinate between repositories
 * Following the Single Responsibility Principle
 */
export interface IService {
  /**
   * Service name for identification
   */
  getName(): string;
}

/**
 * Result wrapper for service operations
 * Provides consistent error handling and response structure
 */
export interface ServiceResult<T = any> {
  success: boolean;
  data?: T;
  error?: string;
  statusCode?: number;
}

/**
 * Validation result interface
 */
export interface ValidationResult {
  valid: boolean;
  error?: string;
  field?: string;
}

/**
 * Response utilities for consistent API responses
 * Provides standardized response formats and error handling
 */

import { NextResponse } from 'next/server';
import { HTTP_STATUS, ERROR_MESSAGES } from './constants';

/**
 * Standard API response structure
 */
interface ApiResponse<T = any> {
  success: boolean;
  data?: T;
  error?: string;
  message?: string;
  pagination?: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
  };
}

/**
 * Creates a success response
 */
export function successResponse<T>(
  data: T,
  status: number = HTTP_STATUS.OK,
  message?: string
): NextResponse<ApiResponse<T>> {
  return NextResponse.json(
    {
      success: true,
      data,
      ...(message && { message }),
    },
    { status }
  );
}

/**
 * Creates an error response
 */
export function errorResponse(
  error: string,
  status: number = HTTP_STATUS.INTERNAL_SERVER_ERROR
): NextResponse<ApiResponse> {
  return NextResponse.json(
    {
      success: false,
      error,
    },
    { status }
  );
}

/**
 * Creates a paginated response
 */
export function paginatedResponse<T>(
  data: T[],
  page: number,
  limit: number,
  total: number
): NextResponse<ApiResponse<T[]>> {
  const totalPages = Math.ceil(total / limit);

  return NextResponse.json(
    {
      success: true,
      data,
      pagination: {
        page,
        limit,
        total,
        totalPages,
      },
    },
    { status: HTTP_STATUS.OK }
  );
}

/**
 * Handles common API errors and returns appropriate response
 */
export function handleApiError(error: unknown): NextResponse<ApiResponse> {
  console.error('API Error:', error);

  // Handle specific error types
  if (error instanceof Error) {
    // Mongoose validation errors
    if ('name' in error && error.name === 'ValidationError') {
      return errorResponse(
        `Validation error: ${error.message}`,
        HTTP_STATUS.BAD_REQUEST
      );
    }

    // Mongoose duplicate key errors
    if ('code' in error && error.code === 11000) {
      return errorResponse(
        'A record with this value already exists',
        HTTP_STATUS.CONFLICT
      );
    }

    // JSON parse errors
    if (error instanceof SyntaxError) {
      return errorResponse(
        ERROR_MESSAGES.INVALID_JSON,
        HTTP_STATUS.BAD_REQUEST
      );
    }
  }

  // Generic error
  return errorResponse(
    ERROR_MESSAGES.INTERNAL_SERVER_ERROR,
    HTTP_STATUS.INTERNAL_SERVER_ERROR
  );
}

/**
 * Unauthorized response
 */
export function unauthorizedResponse(
  message: string = ERROR_MESSAGES.UNAUTHORIZED
): NextResponse<ApiResponse> {
  return errorResponse(message, HTTP_STATUS.UNAUTHORIZED);
}

/**
 * Forbidden response
 */
export function forbiddenResponse(
  message: string = ERROR_MESSAGES.FORBIDDEN
): NextResponse<ApiResponse> {
  return errorResponse(message, HTTP_STATUS.FORBIDDEN);
}

/**
 * Not found response
 */
export function notFoundResponse(
  message: string = ERROR_MESSAGES.NOT_FOUND
): NextResponse<ApiResponse> {
  return errorResponse(message, HTTP_STATUS.NOT_FOUND);
}

/**
 * Bad request response
 */
export function badRequestResponse(
  message: string = ERROR_MESSAGES.INVALID_INPUT
): NextResponse<ApiResponse> {
  return errorResponse(message, HTTP_STATUS.BAD_REQUEST);
}

/**
 * Conflict response
 */
export function conflictResponse(message: string): NextResponse<ApiResponse> {
  return errorResponse(message, HTTP_STATUS.CONFLICT);
}

/**
 * Created response
 */
export function createdResponse<T>(
  data: T,
  message?: string
): NextResponse<ApiResponse<T>> {
  return successResponse(data, HTTP_STATUS.CREATED, message);
}

/**
 * No content response (204)
 */
export function noContentResponse(): NextResponse {
  return new NextResponse(null, { status: HTTP_STATUS.NO_CONTENT });
}

/**
 * Authentication and authorization utilities
 * Centralized auth helpers for consistent security checks
 */

import { auth } from '@/auth';
import { USER_ROLES } from './constants';
import { logger } from './logger';

/**
 * Gets the current authenticated session
 */
export async function getAuthSession() {
  try {
    const session = await auth();
    return session;
  } catch (error) {
    logger.error('Failed to get auth session', error);
    return null;
  }
}

/**
 * Checks if user is authenticated
 */
export async function isAuthenticated(): Promise<boolean> {
  const session = await getAuthSession();
  return !!session?.user?.email;
}

/**
 * Gets the authenticated user's email
 */
export async function getAuthenticatedUserEmail(): Promise<string | null> {
  const session = await getAuthSession();
  return session?.user?.email || null;
}

/**
 * Checks if the authenticated user has admin role
 */
export async function isAdmin(): Promise<boolean> {
  const session = await getAuthSession();
  if (!session?.user) return false;

  const userRole = (session.user as any).role;
  return userRole === USER_ROLES.ADMIN;
}

/**
 * Requires authentication - throws if not authenticated
 */
export async function requireAuth(): Promise<string> {
  const email = await getAuthenticatedUserEmail();

  if (!email) {
    throw new Error('Authentication required');
  }

  return email;
}

/**
 * Requires admin role - throws if not admin
 */
export async function requireAdmin(): Promise<void> {
  const isUserAdmin = await isAdmin();

  if (!isUserAdmin) {
    throw new Error('Admin privileges required');
  }
}

/**
 * Validates session and returns user email
 * Used in API routes for authentication checks
 */
export async function validateSession(): Promise<{
  valid: boolean;
  email?: string;
  error?: string;
}> {
  try {
    const session = await auth();

    if (!session?.user?.email) {
      return {
        valid: false,
        error: 'No active session',
      };
    }

    return {
      valid: true,
      email: session.user.email,
    };
  } catch (error) {
    logger.error('Session validation failed', error);
    return {
      valid: false,
      error: 'Session validation error',
    };
  }
}

/**
 * Checks if user has access to a specific project
 * Used for authorization checks on project resources
 */
export async function hasProjectAccess(
  userEmail: string,
  project: { userId: string; collaborators?: string[] }
): Promise<boolean> {
  // Owner has access
  if (project.userId === userEmail) {
    return true;
  }

  // Check if user is a collaborator
  if (project.collaborators && project.collaborators.includes(userEmail)) {
    return true;
  }

  return false;
}

/**
 * Checks if user is the owner of a project
 */
export function isProjectOwner(userEmail: string, project: { userId: string }): boolean {
  return project.userId === userEmail;
}

/**
 * Hashes a password using bcrypt
 */
export async function hashPassword(password: string): Promise<string> {
  const bcrypt = await import('bcryptjs');
  const saltRounds = 12;
  return bcrypt.hash(password, saltRounds);
}

/**
 * Verifies a password against a hash
 */
export async function verifyPassword(password: string, hash: string): Promise<boolean> {
  const bcrypt = await import('bcryptjs');
  return bcrypt.compare(password, hash);
}

/**
 * Generates a random session token
 */
export function generateSessionToken(): string {
  const crypto = require('crypto');
  return crypto.randomBytes(32).toString('base64');
}

/**
 * Data Generator
 * 
 * This file now serves as a facade/adapter to the new SOLID-compliant architecture.
 * The actual generation logic has been refactored into:
 * - Individual field generator classes (Strategy Pattern) in src/services/fieldGenerators/
 * - A factory for managing generators (Factory Pattern)
 * - A service for orchestrating data generation (Service Pattern)
 * 
 * This maintains backward compatibility while following SOLID principles:
 * - Single Responsibility: Each generator handles one field type
 * - Open/Closed: New field types can be added without modifying existing code
 * - Liskov Substitution: All generators implement IFieldGenerator
 * - Interface Segregation: Focused, minimal interfaces
 * - Dependency Inversion: Depends on abstractions (interfaces), not concretions
 */

import { DataGeneratorService } from '@/services/DataGeneratorService';
import { ResourceField } from '@/types';

// Re-export constants for backward compatibility
export { FAKER_METHODS, VALID_STATUS_CODES } from '@/constants/fieldTypes';

// Create a singleton instance of the data generator service
const dataGeneratorService = new DataGeneratorService();

/**
 * Generate a value for a single field
 * @param field - The field configuration
 * @param allData - Optional context data for relations
 * @returns The generated value
 * 
 * This function now delegates to the new DataGeneratorService which uses
 * the Strategy Pattern to handle different field types.
 */
export function generateFieldValue(field: ResourceField, allData?: any): any {
    return dataGeneratorService.generateFieldValue(field, allData);
}

/**
 * Logger utility for consistent logging across the application
 * Provides structured logging with different severity levels
 */

type LogLevel = 'info' | 'warn' | 'error' | 'debug';

interface LogContext {
  [key: string]: any;
}

class Logger {
  private shouldLog = process.env.NODE_ENV === 'development' || process.env.NODE_ENV === 'test';
  // Reserved for future production-specific logging behavior

  /**
   * Formats the log message with timestamp and context
   */
  private formatLog(level: LogLevel, message: string, context?: LogContext): string {
    const timestamp = new Date().toISOString();
    let contextStr = '';
    if (context) {
      try {
        contextStr = ` | ${JSON.stringify(context)}`;
      } catch (error) {
        // Handle circular references or other JSON errors
        contextStr = ` | [Circular or Invalid JSON]`;
      }
    }
    return `[${timestamp}] [${level.toUpperCase()}] ${message}${contextStr}`;
  }

  /**
   * Info level logging
   */
  info(message: string, context?: LogContext): void {
    if (this.shouldLog) {
      console.log(this.formatLog('info', message, context));
    }
  }

  /**
   * Warning level logging
   */
  warn(message: string, context?: LogContext): void {
    console.warn(this.formatLog('warn', message, context));
  }

  /**
   * Error level logging
   */
  error(message: string, error?: Error | unknown, context?: LogContext): void {
    const errorContext = {
      ...context,
      ...(error instanceof Error && {
        errorName: error.name,
        errorMessage: error.message,
        errorStack: this.shouldLog ? error.stack : undefined,
      }),
    };
    console.error(this.formatLog('error', message, errorContext));
  }

  /**
   * Debug level logging (only in development)
   */
  debug(message: string, context?: LogContext): void {
    if (this.shouldLog) {
      console.debug(this.formatLog('debug', message, context));
    }
  }

  /**
   * Log API request
   */
  logRequest(method: string, path: string, context?: LogContext): void {
    this.info(`${method} ${path}`, context);
  }

  /**
   * Log API response
   */
  logResponse(method: string, path: string, status: number, duration?: number): void {
    const context = duration ? { duration: `${duration}ms` } : undefined;
    this.info(`${method} ${path} - ${status}`, context);
  }

  /**
   * Log database operation
   */
  logDbOperation(operation: string, collection: string, context?: LogContext): void {
    this.debug(`DB ${operation} on ${collection}`, context);
  }

  /**
   * Log authentication event
   */
  logAuth(event: string, email?: string, success: boolean = true): void {
    this.info(`Auth: ${event}`, { email, success });
  }

  /**
   * Log validation error
   */
  logValidationError(field: string, error: string, context?: LogContext): void {
    this.warn(`Validation error on ${field}: ${error}`, context);
  }
}

// Export singleton instance
export const logger = new Logger();

// Export type for use in other files
export type { LogLevel, LogContext };

/**
 * Validation utilities for input sanitization and validation
 * Enterprise-grade validation with comprehensive error handling
 */

import { ERROR_MESSAGES, VALIDATION } from './constants';

/**
 * Validates email format
 */
export function validateEmail(email: string): { valid: boolean; error?: string } {
  if (!email || typeof email !== 'string') {
    return { valid: false, error: ERROR_MESSAGES.INVALID_EMAIL };
  }

  const trimmedEmail = email.trim();
  if (!VALIDATION.EMAIL_REGEX.test(trimmedEmail)) {
    return { valid: false, error: ERROR_MESSAGES.INVALID_EMAIL };
  }

  return { valid: true };
}

/**
 * Validates password strength
 */
export function validatePassword(password: string): { valid: boolean; error?: string } {
  if (!password || typeof password !== 'string') {
    return { valid: false, error: ERROR_MESSAGES.INVALID_PASSWORD };
  }

  if (password.length < VALIDATION.PASSWORD_MIN_LENGTH) {
    return { valid: false, error: ERROR_MESSAGES.PASSWORD_TOO_SHORT };
  }

  if (!VALIDATION.PASSWORD_REGEX.test(password)) {
    return { valid: false, error: ERROR_MESSAGES.PASSWORD_WEAK };
  }

  return { valid: true };
}

/**
 * Validates resource name format
 */
export function validateResourceName(name: string): { valid: boolean; error?: string } {
  if (!name || typeof name !== 'string') {
    return { valid: false, error: 'Resource name is required' };
  }

  const trimmedName = name.trim().toLowerCase();

  if (trimmedName.length === 0) {
    return { valid: false, error: 'Resource name cannot be empty' };
  }

  if (trimmedName.includes('/')) {
    return { 
      valid: false, 
      error: 'Resource name cannot contain slashes. Use Custom APIs for multi-segment paths.' 
    };
  }

  if (!VALIDATION.RESOURCE_NAME_REGEX.test(trimmedName)) {
    return { 
      valid: false, 
      error: 'Resource name must start with a letter and contain only lowercase letters, numbers, hyphens, or underscores' 
    };
  }

  return { valid: true };
}

/**
 * Validates JSON string
 */
export function validateJSON(jsonString: string): { valid: boolean; error?: string; data?: any } {
  if (!jsonString || jsonString.trim() === '') {
    return { valid: true, data: null }; // Empty JSON is valid (optional)
  }

  try {
    const parsed = JSON.parse(jsonString);

    // Check size
    if (JSON.stringify(parsed).length > VALIDATION.MAX_JSON_SIZE) {
      return { valid: false, error: 'JSON payload too large (max 1MB)' };
    }

    return { valid: true, data: parsed };
  } catch (error) {
    return { 
      valid: false, 
      error: ERROR_MESSAGES.INVALID_JSON + '. Check for syntax errors, missing commas, or quotes.' 
    };
  }
}

/**
 * Sanitizes string input to prevent XSS and injection attacks
 */
export function sanitizeString(input: string, maxLength?: number): string {
  if (!input || typeof input !== 'string') {
    return '';
  }

  let sanitized = input
    .trim()
    .replace(/[<>]/g, '') // Remove potential HTML tags
    .replace(/javascript:/gi, '') // Remove javascript: protocol
    .replace(/on\w+=/gi, ''); // Remove event handlers

  if (maxLength && sanitized.length > maxLength) {
    sanitized = sanitized.substring(0, maxLength);
  }

  return sanitized;
}

/**
 * Validates MongoDB ObjectId format
 */
export function isValidObjectId(id: string): boolean {
  return /^[0-9a-fA-F]{24}$/.test(id);
}

/**
 * Validates UUID format
 */
export function isValidUUID(uuid: string): boolean {
  return /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(uuid);
}

/**
 * Validates HTTP status code
 */
export function isValidStatusCode(code: number): boolean {
  return Number.isInteger(code) && code >= 100 && code < 600;
}

/**
 * Validates API path format
 */
export function validateApiPath(path: string): { valid: boolean; error?: string } {
  if (!path || typeof path !== 'string') {
    return { valid: false, error: 'API path is required' };
  }

  // Check for whitespace before trimming (to catch paths like '/   ')
  if (/\s/.test(path)) {
    return { valid: false, error: 'API path contains invalid characters' };
  }

  const trimmedPath = path.trim();

  if (trimmedPath.length === 0) {
    return { valid: false, error: 'API path cannot be empty' };
  }

  // Path should start with /
  if (!trimmedPath.startsWith('/')) {
    return { valid: false, error: 'API path must start with /' };
  }

  // Check for other invalid characters
  if (/[<>'"\\]/.test(trimmedPath)) {
    return { valid: false, error: 'API path contains invalid characters' };
  }

  return { valid: true };
}

/**
 * Validates pagination parameters
 */
export function validatePagination(page?: string, limit?: string): {
  page: number;
  limit: number;
  error?: string;
} {
  const defaultPage = 1;
  const defaultLimit = 10;
  const maxLimit = 100;

  let parsedPage = defaultPage;
  let parsedLimit = defaultLimit;

  if (page) {
    parsedPage = parseInt(page, 10);
    if (isNaN(parsedPage) || parsedPage < 1) {
      return { page: defaultPage, limit: defaultLimit, error: 'Invalid page number' };
    }
  }

  if (limit) {
    parsedLimit = parseInt(limit, 10);
    if (isNaN(parsedLimit) || parsedLimit < 1) {
      return { page: parsedPage, limit: defaultLimit, error: 'Invalid limit' };
    }
    if (parsedLimit > maxLimit) {
      parsedLimit = maxLimit;
    }
  }

  return { page: parsedPage, limit: parsedLimit };
}

/**
 * Validates required fields in an object
 */
export function validateRequiredFields<T extends Record<string, any>>(
  obj: T,
  requiredFields: (keyof T)[]
): { valid: boolean; error?: string; missing?: string[] } {
  const missing: string[] = [];

  for (const field of requiredFields) {
    if (obj[field] === undefined || obj[field] === null || obj[field] === '') {
      missing.push(String(field));
    }
  }

  if (missing.length > 0) {
    return {
      valid: false,
      error: `${ERROR_MESSAGES.MISSING_REQUIRED_FIELDS}: ${missing.join(', ')}`,
      missing,
    };
  }

  return { valid: true };
}

/**
 * Validates HTTP method
 */
export function isValidHttpMethod(method: string): boolean {
  const validMethods = ['GET', 'POST', 'PUT', 'PATCH', 'DELETE', 'OPTIONS', 'HEAD'];
  return validMethods.includes(method.toUpperCase());
}

/**
 * Validates field type
 */
export function isValidFieldType(type: string): boolean {
  const validTypes = ['string', 'number', 'boolean', 'date', 'email', 'uuid', 'image', 'relation'];
  return validTypes.includes(type);
}

