// API Schema
export interface IAPI extends Document {
  id: string;
  name: string;
  path: string;
  method: string;
  statusCode: number;
  requestBody?: any;
  responseBody: any;
  queryParams?: Array<{
    key: string;
    value: string;
    required: boolean;
  }>;
  webhookUrl?: string;
  projectId: string;
  conditionalResponse?: {
    condition: {
      type: 'header' | 'query' | 'body' | 'dependentApi';
      key?: string;
      operator: 'equals' | 'notEquals' | 'contains' | 'greaterThan' | 'lessThan' | 'exists';
      value?: any;
      dependentApiId?: string;
