import { IRepository } from '@/interfaces/IRepository';
import { Model, Document } from 'mongoose';
import connectDB from '@/lib/mongodb';
import { logger } from '@/lib/logger';

/**
 * Base MongoDB repository implementation
 * Provides common CRUD operations for Mongoose models
 * Following the Repository Pattern to abstract data access
 * 
 * Generic type T represents the document interface
 */
export abstract class BaseRepository<T extends Document> implements IRepository<T> {
  constructor(
    protected model: Model<T>,
    protected modelName: string
  ) {}

  /**
   * Ensure database connection before operations
   */
  protected async ensureConnection(): Promise<void> {
    await connectDB();
  }

  async findById(id: string): Promise<T | null> {
    await this.ensureConnection();
    logger.logDbOperation('findById', this.modelName, { id });

    try {
      const query = this.model.findOne({ id } as any);
      // Check if lean method exists (real Mongoose query) before calling
      const result = typeof (query as any).lean === 'function' 
        ? await (query as any).lean()
        : await query;
      return result as T | null;
    } catch (error) {
      logger.error(`Error finding ${this.modelName} by id`, error);
      throw error;
    }
  }

  async find(filter?: Record<string, any>): Promise<T[]> {
    await this.ensureConnection();
    logger.logDbOperation('find', this.modelName, filter);

    try {
      const query = this.model.find(filter || {});
      // Check if lean method exists (real Mongoose query) before calling
      const results = typeof (query as any).lean === 'function' 
        ? await (query as any).lean()
        : await query;
      return results as T[];
    } catch (error) {
      logger.error(`Error finding ${this.modelName}`, error);
      throw error;
    }
  }

  async findOne(filter: Record<string, any>): Promise<T | null> {
    await this.ensureConnection();
    logger.logDbOperation('findOne', this.modelName, filter);

    try {
      const query = this.model.findOne(filter);
      // Check if lean method exists (real Mongoose query) before calling
      const result = typeof (query as any).lean === 'function' 
        ? await (query as any).lean()
        : await query;
      return result as T | null;
    } catch (error) {
      logger.error(`Error finding one ${this.modelName}`, error);
      throw error;
    }
  }

  async create(data: Partial<T>): Promise<T> {
    await this.ensureConnection();
    logger.logDbOperation('create', this.modelName, data);

    try {
      const document = new this.model(data);
      const result = await document.save();
      return result as T;
    } catch (error) {
      logger.error(`Error creating ${this.modelName}`, error);
      throw error;
    }
  }

  async update(id: string, data: Partial<T>): Promise<T | null> {
    await this.ensureConnection();
    logger.logDbOperation('update', this.modelName, { id, ...data });

    try {
      const query = this.model.findOneAndUpdate(
        { id } as any,
        data,
        { new: true }
      );
      // Check if lean method exists (real Mongoose query) before calling
      const result = typeof (query as any).lean === 'function' 
        ? await (query as any).lean()
        : await query;
      return result as T | null;
    } catch (error) {
      logger.error(`Error updating ${this.modelName}`, error);
      throw error;
    }
  }

  async delete(id: string): Promise<boolean> {
    await this.ensureConnection();
    logger.logDbOperation('delete', this.modelName, { id });

    try {
      const result = await this.model.deleteOne({ id } as any);
      return result.deletedCount > 0;
    } catch (error) {
      logger.error(`Error deleting ${this.modelName}`, error);
      throw error;
    }
  }

  async exists(filter: Record<string, any>): Promise<boolean> {
    await this.ensureConnection();

    try {
      const count = await this.model.countDocuments(filter);
      return count > 0;
    } catch (error) {
      logger.error(`Error checking existence in ${this.modelName}`, error);
      throw error;
    }
  }

  async count(filter?: Record<string, any>): Promise<number> {
    await this.ensureConnection();

    try {
      return await this.model.countDocuments(filter || {});
    } catch (error) {
      logger.error(`Error counting ${this.modelName}`, error);
      throw error;
    }
  }
}

import { BaseRepository } from './BaseRepository';
import { Database, IDatabase } from '@/models';

/**
 * Database repository
 * Handles data access for generated resource data
 */
export class DatabaseRepository extends BaseRepository<IDatabase> {
  constructor() {
    super(Database, 'Database');
  }

  /**
   * Find data by resource name
   */
  async findByResourceName(resourceName: string): Promise<IDatabase | null> {
    await this.ensureConnection();
    return this.findOne({ resourceName });
  }

  /**
   * Get data for a resource
   */
  async getData(resourceName: string): Promise<any[]> {
    await this.ensureConnection();

    const result = await this.findByResourceName(resourceName);
    return result?.data || [];
  }

  /**
   * Set data for a resource (creates or updates)
   */
  async setData(resourceName: string, data: any[]): Promise<IDatabase> {
    await this.ensureConnection();

    const existing = await this.findByResourceName(resourceName);

    if (existing) {
      const result = await Database.findOneAndUpdate(
        { resourceName },
        { data, updatedAt: new Date() },
        { new: true }
      );
      return result as IDatabase;
    } else {
      return await this.create({ resourceName, data } as Partial<IDatabase>);
    }
  }

  /**
   * Append data to a resource
   */
  async appendData(resourceName: string, newData: any[]): Promise<IDatabase | null> {
    await this.ensureConnection();

    const result = await Database.findOneAndUpdate(
      { resourceName },
      { 
        $push: { data: { $each: newData } },
        updatedAt: new Date()
      },
      { new: true, upsert: true }
    );

    return result as IDatabase | null;
  }

  /**
   * Clear data for a resource
   */
  async clearData(resourceName: string): Promise<boolean> {
    await this.ensureConnection();

    const result = await Database.findOneAndUpdate(
      { resourceName },
      { data: [], updatedAt: new Date() },
      { new: true }
    );

    return !!result;
  }

  /**
   * Delete database entry for a resource
   */
  async deleteByResourceName(resourceName: string): Promise<boolean> {
    await this.ensureConnection();

    const result = await Database.deleteOne({ resourceName });
    return result.deletedCount > 0;
  }
}

import { BaseRepository } from './BaseRepository';
import { Project, IProject } from '@/models';

/**
 * Project repository
 * Handles data access for Project entities
 * Extends BaseRepository with project-specific operations
 */
export class ProjectRepository extends BaseRepository<IProject> {
  constructor() {
    super(Project, 'Project');
  }

  /**
   * Find projects by user email (owner or collaborator)
   */
  async findByUser(email: string): Promise<IProject[]> {
    await this.ensureConnection();

    return this.find({
      $or: [
        { userId: email },
        { collaborators: email }
      ]
    });
  }

  /**
   * Find projects owned by a user
   */
  async findByOwner(userId: string): Promise<IProject[]> {
    await this.ensureConnection();
    return this.find({ userId });
  }

  /**
   * Check if a project name exists for a user
   */
  async nameExistsForUser(userId: string, name: string): Promise<boolean> {
    await this.ensureConnection();

    return this.exists({
      userId,
      name: { $regex: new RegExp(`^${name}$`, 'i') }
    });
  }

  /**
   * Add a collaborator to a project
   */
  async addCollaborator(projectId: string, email: string): Promise<IProject | null> {
    await this.ensureConnection();

    const query = Project.findOneAndUpdate(
      { id: projectId },
      { $addToSet: { collaborators: email } },
      { new: true }
    );

    // Check if lean method exists (real Mongoose query) before calling
    const result = typeof (query as any).lean === 'function' 
      ? await (query as any).lean()
      : await query;

    return result as IProject | null;
  }

  /**
   * Remove a collaborator from a project
   */
  async removeCollaborator(projectId: string, email: string): Promise<IProject | null> {
    await this.ensureConnection();

    const query = Project.findOneAndUpdate(
      { id: projectId },
      { $pull: { collaborators: email } },
      { new: true }
    );

    // Check if lean method exists (real Mongoose query) before calling
    const result = typeof (query as any).lean === 'function' 
      ? await (query as any).lean()
      : await query;

    return result as IProject | null;
  }

  /**
   * Check if user has access to a project (owner or collaborator)
   */
  async hasAccess(projectId: string, email: string): Promise<boolean> {
    await this.ensureConnection();

    return this.exists({
      id: projectId,
      $or: [
        { userId: email },
        { collaborators: email }
      ]
    });
  }
}

import { BaseRepository } from './BaseRepository';
import { Resource, IResource } from '@/models';

/**
 * Resource repository
 * Handles data access for Resource entities
 */
export class ResourceRepository extends BaseRepository<IResource> {
  constructor() {
    super(Resource, 'Resource');
  }

  /**
   * Find resources by project ID
   */
  async findByProject(projectId: string): Promise<IResource[]> {
    await this.ensureConnection();
    return this.find({ projectId });
  }

  /**
   * Find resource by name and project
   */
  async findByNameAndProject(name: string, projectId: string): Promise<IResource | null> {
    await this.ensureConnection();

    return this.findOne({
      name: { $regex: new RegExp(`^${name}$`, 'i') },
      projectId
    });
  }

  /**
   * Check if resource name exists in project
   */
  async nameExistsInProject(name: string, projectId: string): Promise<boolean> {
    await this.ensureConnection();

    return this.exists({
      name: { $regex: new RegExp(`^${name}$`, 'i') },
      projectId
    });
  }

  /**
   * Delete all resources for a project
   */
  async deleteByProject(projectId: string): Promise<number> {
    await this.ensureConnection();

    const result = await Resource.deleteMany({ projectId });
    return result.deletedCount;
  }
}

import { IDataGenerator } from '@/interfaces/IDataGenerator';
import { IFieldGenerator } from '@/interfaces/IFieldGenerator';
import { FieldGeneratorFactory } from './fieldGenerators/FieldGeneratorFactory';
import { faker } from '@faker-js/faker';

/**
 * Main data generation service
 * Coordinates field generators to produce complete data records
 * Following Single Responsibility Principle - only handles orchestration
 */
export class DataGeneratorService implements IDataGenerator {
  private factory: FieldGeneratorFactory;

  constructor(fakerInstance = faker) {
    this.factory = new FieldGeneratorFactory(fakerInstance);
  }

  /**
   * Generate a value for a single field
   * @param field - The field configuration
   * @param allData - Optional context data for relations
   * @returns The generated value
   */
  generateFieldValue(field: any, allData?: any): any {
    const generator = this.factory.get(field.type);

    if (!generator) {
      throw new Error(`No generator found for field type: ${field.type}`);
    }

    return generator.generate(field, allData);
  }

  /**
   * Generate multiple records for a resource
   * @param resource - The resource schema
   * @param count - Number of records to generate
   * @returns Array of generated records
   */
  generateRecords(resource: any, count: number): any[] {
    const records: any[] = [];

    for (let i = 0; i < count; i++) {
      const record: any = {};

      // Generate value for each field
      for (const field of resource.fields) {
        record[field.name] = this.generateFieldValue(field);
      }

      records.push(record);
    }

    return records;
  }

  /**
   * Register a new field generator
   * @param generator - The generator to register
   */
  registerGenerator(generator: IFieldGenerator): void {
    this.factory.register(generator);
  }

  /**
   * Get all supported field types
   * @returns Array of supported types
   */
  getSupportedTypes(): string[] {
    return this.factory.getSupportedTypes();
  }
}

import { IService, ServiceResult, ValidationResult } from '@/interfaces/IService';
import { ProjectRepository } from '@/repositories/ProjectRepository';
import { IProject } from '@/models';
import { validateRequiredFields, sanitizeString } from '@/lib/validation';
import { logger } from '@/lib/logger';
import { randomUUID } from 'crypto';

/**
 * Project service
 * Handles all business logic related to projects
 * Following Single Responsibility Principle - only handles project operations
 */
export class ProjectService implements IService {
  constructor(private projectRepository: ProjectRepository) {}

  getName(): string {
    return 'ProjectService';
  }

  /**
   * Get all projects for a user (owned or collaborating)
   */
  async getProjectsForUser(email: string): Promise<ServiceResult<IProject[]>> {
    try {
      const projects = await this.projectRepository.findByUser(email);

      logger.info(`Found ${projects.length} projects for user`, { email });

      return {
        success: true,
        data: projects,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error fetching projects', error);
      return {
        success: false,
        error: 'Failed to fetch projects',
        statusCode: 500
      };
    }
  }

  /**
   * Get a single project by ID
   */
  async getProjectById(id: string, userEmail: string): Promise<ServiceResult<IProject>> {
    try {
      // Check if user has access to this project
      const hasAccess = await this.projectRepository.hasAccess(id, userEmail);

      if (!hasAccess) {
        return {
          success: false,
          error: 'Project not found or access denied',
          statusCode: 404
        };
      }

      const project = await this.projectRepository.findById(id);

      if (!project) {
        return {
          success: false,
          error: 'Project not found',
          statusCode: 404
        };
      }

      return {
        success: true,
        data: project,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error fetching project', error);
      return {
        success: false,
        error: 'Failed to fetch project',
        statusCode: 500
      };
    }
  }

  /**
   * Create a new project
   */
  async createProject(
    name: string,
    userId: string,
    description?: string
  ): Promise<ServiceResult<IProject>> {
    try {
      // Validate required fields
      const validation = this.validateProjectData({ name });
      if (!validation.valid) {
        return {
          success: false,
          error: validation.error,
          statusCode: 400
        };
      }

      // Sanitize inputs
      const sanitizedName = sanitizeString(name, 100);
      const sanitizedDescription = description ? sanitizeString(description, 500) : '';

      if (!sanitizedName) {
        return {
          success: false,
          error: 'Project name cannot be empty',
          statusCode: 400
        };
      }

      // Check for duplicate name
      const exists = await this.projectRepository.nameExistsForUser(userId, sanitizedName);

      if (exists) {
        logger.warn('Duplicate project name attempted', { name: sanitizedName, userId });
        return {
          success: false,
          error: 'Project with this name already exists',
          statusCode: 409
        };
      }

      // Create project
      const project = await this.projectRepository.create({
        id: randomUUID(),
        name: sanitizedName,
        description: sanitizedDescription,
        userId,
        collaborators: [],
        isPublic: false
      } as Partial<IProject>);

      logger.info('Project created successfully', { id: project.id, userId });

      return {
        success: true,
        data: project,
        statusCode: 201
      };
    } catch (error) {
      logger.error('Error creating project', error);
      return {
        success: false,
        error: 'Failed to create project',
        statusCode: 500
      };
    }
  }

  /**
   * Update a project
   */
  async updateProject(
    id: string,
    userEmail: string,
    updates: Partial<{ name: string; description: string; isPublic: boolean }>
  ): Promise<ServiceResult<IProject>> {
    try {
      // Check if user owns this project
      const project = await this.projectRepository.findById(id);

      if (!project) {
        return {
          success: false,
          error: 'Project not found',
          statusCode: 404
        };
      }

      if (project.userId !== userEmail) {
        return {
          success: false,
          error: 'Only the project owner can update it',
          statusCode: 403
        };
      }

      // Sanitize inputs
      const sanitizedUpdates: any = {};

      if (updates.name) {
        const sanitizedName = sanitizeString(updates.name, 100);
        if (!sanitizedName) {
          return {
            success: false,
            error: 'Project name cannot be empty',
            statusCode: 400
          };
        }

        // Check for duplicate name
        const nameExists = await this.projectRepository.nameExistsForUser(userEmail, sanitizedName);
        if (nameExists && project.name !== sanitizedName) {
          return {
            success: false,
            error: 'Project with this name already exists',
            statusCode: 409
          };
        }

        sanitizedUpdates.name = sanitizedName;
      }

      if (updates.description !== undefined) {
        sanitizedUpdates.description = updates.description ? sanitizeString(updates.description, 500) : '';
      }

      if (updates.isPublic !== undefined) {
        sanitizedUpdates.isPublic = updates.isPublic;
      }

      const updatedProject = await this.projectRepository.update(id, sanitizedUpdates);

      if (!updatedProject) {
        return {
          success: false,
          error: 'Failed to update project',
          statusCode: 500
        };
      }

      logger.info('Project updated successfully', { id });

      return {
        success: true,
        data: updatedProject,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error updating project', error);
      return {
        success: false,
        error: 'Failed to update project',
        statusCode: 500
      };
    }
  }

  /**
   * Delete a project
   */
  async deleteProject(id: string, userEmail: string): Promise<ServiceResult<void>> {
    try {
      const project = await this.projectRepository.findById(id);

      if (!project) {
        return {
          success: false,
          error: 'Project not found',
          statusCode: 404
        };
      }

      if (project.userId !== userEmail) {
        return {
          success: false,
          error: 'Only the project owner can delete it',
          statusCode: 403
        };
      }

      const deleted = await this.projectRepository.delete(id);

      if (!deleted) {
        return {
          success: false,
          error: 'Failed to delete project',
          statusCode: 500
        };
      }

      logger.info('Project deleted successfully', { id });

      return {
        success: true,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error deleting project', error);
      return {
        success: false,
        error: 'Failed to delete project',
        statusCode: 500
      };
    }
  }

  /**
   * Add a collaborator to a project
   */
  async addCollaborator(projectId: string, ownerEmail: string, collaboratorEmail: string): Promise<ServiceResult<IProject>> {
    try {
      const project = await this.projectRepository.findById(projectId);

      if (!project) {
        return {
          success: false,
          error: 'Project not found',
          statusCode: 404
        };
      }

      if (project.userId !== ownerEmail) {
        return {
          success: false,
          error: 'Only the project owner can add collaborators',
          statusCode: 403
        };
      }

      if (project.userId === collaboratorEmail) {
        return {
          success: false,
          error: 'Owner is already a collaborator',
          statusCode: 400
        };
      }

      if (project.collaborators?.includes(collaboratorEmail)) {
        return {
          success: false,
          error: 'User is already a collaborator',
          statusCode: 409
        };
      }

      const updatedProject = await this.projectRepository.addCollaborator(projectId, collaboratorEmail);

      if (!updatedProject) {
        return {
          success: false,
          error: 'Failed to add collaborator',
          statusCode: 500
        };
      }

      logger.info('Collaborator added successfully', { projectId, collaboratorEmail });

      return {
        success: true,
        data: updatedProject,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error adding collaborator', error);
      return {
        success: false,
        error: 'Failed to add collaborator',
        statusCode: 500
      };
    }
  }

  /**
   * Remove a collaborator from a project
   */
  async removeCollaborator(projectId: string, ownerEmail: string, collaboratorEmail: string): Promise<ServiceResult<IProject>> {
    try {
      const project = await this.projectRepository.findById(projectId);

      if (!project) {
        return {
          success: false,
          error: 'Project not found',
          statusCode: 404
        };
      }

      if (project.userId !== ownerEmail) {
        return {
          success: false,
          error: 'Only the project owner can remove collaborators',
          statusCode: 403
        };
      }

      const updatedProject = await this.projectRepository.removeCollaborator(projectId, collaboratorEmail);

      if (!updatedProject) {
        return {
          success: false,
          error: 'Failed to remove collaborator',
          statusCode: 500
        };
      }

      logger.info('Collaborator removed successfully', { projectId, collaboratorEmail });

      return {
        success: true,
        data: updatedProject,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error removing collaborator', error);
      return {
        success: false,
        error: 'Failed to remove collaborator',
        statusCode: 500
      };
    }
  }

  /**
   * Validate project data
   */
  private validateProjectData(data: any): ValidationResult {
    const validation = validateRequiredFields(data, ['name']);
    return validation;
  }
}

import { IService, ServiceResult, ValidationResult } from '@/interfaces/IService';
import { ResourceRepository } from '@/repositories/ResourceRepository';
import { DatabaseRepository } from '@/repositories/DatabaseRepository';
import { IResource } from '@/models';
import { DataGeneratorService } from './DataGeneratorService';
import { validateRequiredFields, validateResourceName, isValidFieldType } from '@/lib/validation';
import { logger } from '@/lib/logger';
import { randomUUID } from 'crypto';

/**
 * Resource service
 * Handles all business logic related to resources and data generation
 * Following Single Responsibility Principle
 */
export class ResourceService implements IService {
  constructor(
    private resourceRepository: ResourceRepository,
    private databaseRepository: DatabaseRepository,
    private dataGenerator: DataGeneratorService
  ) {}

  getName(): string {
    return 'ResourceService';
  }

  /**
   * Get all resources
   */
  async getAllResources(): Promise<ServiceResult<IResource[]>> {
    try {
      const resources = await this.resourceRepository.find();

      logger.info(`Fetched ${resources.length} resources`);

      return {
        success: true,
        data: resources,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error fetching resources', error);
      return {
        success: false,
        error: 'Failed to fetch resources',
        statusCode: 500
      };
    }
  }

  /**
   * Get resources by project ID
   */
  async getResourcesByProject(projectId: string): Promise<ServiceResult<IResource[]>> {
    try {
      const resources = await this.resourceRepository.findByProject(projectId);

      return {
        success: true,
        data: resources,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error fetching resources by project', error);
      return {
        success: false,
        error: 'Failed to fetch resources',
        statusCode: 500
      };
    }
  }

  /**
   * Get a single resource by ID
   */
  async getResourceById(id: string): Promise<ServiceResult<IResource>> {
    try {
      const resource = await this.resourceRepository.findById(id);

      if (!resource) {
        return {
          success: false,
          error: 'Resource not found',
          statusCode: 404
        };
      }

      return {
        success: true,
        data: resource,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error fetching resource', error);
      return {
        success: false,
        error: 'Failed to fetch resource',
        statusCode: 500
      };
    }
  }

  /**
   * Create a new resource
   */
  async createResource(
    name: string,
    fields: any[],
    projectId: string
  ): Promise<ServiceResult<IResource>> {
    try {
      // Validate required fields
      const validation = this.validateResourceData({ name, fields, projectId });
      if (!validation.valid) {
        return {
          success: false,
          error: validation.error,
          statusCode: 400
        };
      }

      // Parse fields if it's a string
      let parsedFields = fields;
      if (typeof fields === 'string') {
        try {
          parsedFields = JSON.parse(fields);
        } catch (e) {
          return {
            success: false,
            error: 'Invalid fields format - must be valid JSON',
            statusCode: 400
          };
        }
      }

      // Ensure fields is an array
      if (!Array.isArray(parsedFields)) {
        return {
          success: false,
          error: 'Fields must be an array',
          statusCode: 400
        };
      }

      // Validate resource name
      const nameValidation = validateResourceName(name);
      if (!nameValidation.valid) {
        return {
          success: false,
          error: nameValidation.error,
          statusCode: 400
        };
      }

      // Validate each field
      for (const field of parsedFields) {
        if (!field.name || !field.type) {
          return {
            success: false,
            error: 'Each field must have a name and type',
            statusCode: 400
          };
        }

        if (!isValidFieldType(field.type)) {
          return {
            success: false,
            error: `Invalid field type: ${field.type}`,
            statusCode: 400
          };
        }

        // Ensure field has an ID
        if (!field.id) {
          field.id = randomUUID();
        }
      }

      // Check for duplicate name in project
      const exists = await this.resourceRepository.nameExistsInProject(name, projectId);

      if (exists) {
        logger.warn('Duplicate resource name attempted', { name, projectId });
        return {
          success: false,
          error: 'Resource with this name already exists in project',
          statusCode: 409
        };
      }

      // Create resource
      const resource = await this.resourceRepository.create({
        id: randomUUID(),
        name,
        fields: parsedFields,
        projectId,
        createdAt: new Date()
      } as Partial<IResource>);

      logger.info('Resource created successfully', { id: resource.id, name });

      return {
        success: true,
        data: resource,
        statusCode: 201
      };
    } catch (error) {
      logger.error('Error creating resource', error);
      return {
        success: false,
        error: 'Failed to create resource',
        statusCode: 500
      };
    }
  }

  /**
   * Update a resource
   */
  async updateResource(
    id: string,
    updates: Partial<{ name: string; fields: any[] }>
  ): Promise<ServiceResult<IResource>> {
    try {
      const resource = await this.resourceRepository.findById(id);

      if (!resource) {
        return {
          success: false,
          error: 'Resource not found',
          statusCode: 404
        };
      }

      const sanitizedUpdates: any = {};

      if (updates.name) {
        const nameValidation = validateResourceName(updates.name);
        if (!nameValidation.valid) {
          return {
            success: false,
            error: nameValidation.error,
            statusCode: 400
          };
        }

        // Check for duplicate name
        const nameExists = await this.resourceRepository.nameExistsInProject(updates.name, resource.projectId);
        if (nameExists && resource.name !== updates.name) {
          return {
            success: false,
            error: 'Resource with this name already exists in project',
            statusCode: 409
          };
        }

        sanitizedUpdates.name = updates.name;
      }

      if (updates.fields) {
        // Validate fields
        for (const field of updates.fields) {
          if (!field.name || !field.type) {
            return {
              success: false,
              error: 'Each field must have a name and type',
              statusCode: 400
            };
          }

          if (!isValidFieldType(field.type)) {
            return {
              success: false,
              error: `Invalid field type: ${field.type}`,
              statusCode: 400
            };
          }
        }

        sanitizedUpdates.fields = updates.fields;
      }

      const updatedResource = await this.resourceRepository.update(id, sanitizedUpdates);

      if (!updatedResource) {
        return {
          success: false,
          error: 'Failed to update resource',
          statusCode: 500
        };
      }

      logger.info('Resource updated successfully', { id });

      return {
        success: true,
        data: updatedResource,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error updating resource', error);
      return {
        success: false,
        error: 'Failed to update resource',
        statusCode: 500
      };
    }
  }

  /**
   * Delete a resource
   */
  async deleteResource(id: string): Promise<ServiceResult<void>> {
    try {
      const resource = await this.resourceRepository.findById(id);

      if (!resource) {
        return {
          success: false,
          error: 'Resource not found',
          statusCode: 404
        };
      }

      // Delete associated data
      await this.databaseRepository.deleteByResourceName(resource.name);

      // Delete resource
      const deleted = await this.resourceRepository.delete(id);

      if (!deleted) {
        return {
          success: false,
          error: 'Failed to delete resource',
          statusCode: 500
        };
      }

      logger.info('Resource deleted successfully', { id });

      return {
        success: true,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error deleting resource', error);
      return {
        success: false,
        error: 'Failed to delete resource',
        statusCode: 500
      };
    }
  }

  /**
   * Generate data for a resource
   */
  async generateData(resourceId: string, count: number = 10): Promise<ServiceResult<any[]>> {
    try {
      const resource = await this.resourceRepository.findById(resourceId);

      if (!resource) {
        return {
          success: false,
          error: 'Resource not found',
          statusCode: 404
        };
      }

      // Get all existing data for relations
      const allResources = await this.resourceRepository.find();
      const allData: any = {};

      for (const res of allResources) {
        const data = await this.databaseRepository.getData(res.name);
        allData[res.name] = data;
      }

      // Generate data
      const generatedData: any[] = [];

      for (let i = 0; i < count; i++) {
        const record: any = { id: randomUUID() };

        for (const field of resource.fields) {
          record[field.name] = this.dataGenerator.generateFieldValue(field, allData);
        }

        generatedData.push(record);
      }

      // Save generated data
      await this.databaseRepository.appendData(resource.name, generatedData);

      logger.info('Data generated successfully', { resourceId, count });

      return {
        success: true,
        data: generatedData,
        statusCode: 201
      };
    } catch (error) {
      logger.error('Error generating data', error);
      return {
        success: false,
        error: 'Failed to generate data',
        statusCode: 500
      };
    }
  }

  /**
   * Get data for a resource
   */
  async getResourceData(resourceName: string): Promise<ServiceResult<any[]>> {
    try {
      const data = await this.databaseRepository.getData(resourceName);

      return {
        success: true,
        data,
        statusCode: 200
      };
    } catch (error) {
      logger.error('Error fetching resource data', error);
      return {
        success: false,
        error: 'Failed to fetch resource data',
        statusCode: 500
      };
    }
  }

  /**
   * Validate resource data
   */
  private validateResourceData(data: any): ValidationResult {
    const validation = validateRequiredFields(data, ['name', 'fields', 'projectId']);
    return validation;
  }
}

